"use client";

import { useState } from "react";
import { Save, ArrowLeft, Image as ImageIcon } from "lucide-react";
import Link from "next/link";

export default function HomeEditorPage() {
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState({
    heroTitle: "ENDÜSTRİYEL SOĞUTMA KULELERİ",
    heroSubtitle: "Enerji santralleri ve endüstriyel tesisler için yüksek performanslı hiperbolik ve mekanik soğutma çözümleri.",
    aboutTitle: "MÜHENDİSLİK VE PERFORMANS",
    aboutText: "HubCos, enerji üretiminin kalbinde yer alan hayati soğutma sistemlerini inşa eder. Doğal çekişli hiperbolik kulelerden, paket tip mekanik kulelere kadar endüstriyel soğutma ihtiyaçlarınız için mühendislik harikası yapılar üretiyoruz."
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    // Simulate API call
    setTimeout(() => setLoading(false), 1000);
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="flex items-center gap-4 mb-8">
        <Link 
            href="/admin/content"
            className="p-2 bg-gray-800 rounded-lg text-gray-400 hover:text-white hover:bg-gray-700 transition-colors"
        >
            <ArrowLeft className="w-6 h-6" />
        </Link>
        <div>
            <h1 className="text-3xl font-bold">Ana Sayfa Düzenle</h1>
            <p className="text-gray-400">Site girişindeki metinleri güncelleyin.</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-8">
        
        {/* Hero Section */}
        <div className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
            <h2 className="text-xl font-bold text-[#C5A059] mb-6 flex items-center gap-2">
                1. Hero Bölümü (Üst Alan)
            </h2>
            
            <div className="space-y-6">
                <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Ana Başlık</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.heroTitle}
                        onChange={(e) => setData({...data, heroTitle: e.target.value})}
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Alt Açıklama</label>
                    <textarea 
                        rows={3}
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.heroSubtitle}
                        onChange={(e) => setData({...data, heroSubtitle: e.target.value})}
                    />
                </div>
            </div>
        </div>

        {/* About Section */}
        <div className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
            <h2 className="text-xl font-bold text-[#C5A059] mb-6 flex items-center gap-2">
                2. Hakkımızda Özeti
            </h2>
            
            <div className="space-y-6">
                <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Bölüm Başlığı</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.aboutTitle}
                        onChange={(e) => setData({...data, aboutTitle: e.target.value})}
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Hakkımızda Metni</label>
                    <textarea 
                        rows={5}
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.aboutText}
                        onChange={(e) => setData({...data, aboutText: e.target.value})}
                    />
                </div>
            </div>
        </div>

        <div className="flex justify-end pt-4">
             <button
                type="submit"
                disabled={loading}
                className="bg-[#C5A059] hover:bg-[#b08e4f] text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 transition-all shadow-lg shadow-[#C5A059]/20"
            >
                <Save className="w-5 h-5" />
                {loading ? "Kaydediliyor..." : "Değişiklikleri Kaydet"}
            </button>
        </div>

      </form>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { Save, ArrowLeft, Upload, Globe, Mail, Phone, MapPin } from "lucide-react";
import Link from "next/link";

export default function IdentityEditorPage() {
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState({
    siteTitle: "HubCos - Endüstriyel Soğutma Kuleleri",
    footerText: "Modern mimari ve güvenilir yapı anlayışıyla geleceği inşa ediyoruz.",
    address: "İstanbul, Türkiye",
    phone: "+90 555 123 45 67",
    email: "info@hubcos.com",
    linkedin: "https://linkedin.com/company/hubcos",
    instagram: "https://instagram.com/hubcos"
  });

  useEffect(() => {
    fetch('/api/content')
      .then(res => res.json())
      .then(data => setData(data));
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const res = await fetch('/api/content', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (res.ok) {
        alert("Ayarlar başarıyla kaydedildi!");
      } else {
        alert("Bir hata oluştu.");
      }
    } catch (error) {
      alert("Bağlantı hatası.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="flex items-center gap-4 mb-8">
        <Link 
            href="/admin/content"
            className="p-2 bg-gray-800 rounded-lg text-gray-400 hover:text-white hover:bg-gray-700 transition-colors"
        >
            <ArrowLeft className="w-6 h-6" />
        </Link>
        <div>
            <h1 className="text-3xl font-bold">Site Kimliği & İletişim</h1>
            <p className="text-gray-400">Logo, iletişim bilgileri ve genel site ayarları.</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-8">
        
        {/* General Settings */}
        <div className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
            <h2 className="text-xl font-bold text-[#C5A059] mb-6 flex items-center gap-2">
                <Globe className="w-5 h-5" /> Genel Ayarlar
            </h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                 <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Site Başlığı (Tab Title)</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.siteTitle}
                        onChange={(e) => setData({...data, siteTitle: e.target.value})}
                    />
                </div>
                 <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Logo Yükle</label>
                    <div className="border hover:border-[#C5A059] border-gray-700 border-dashed rounded-xl px-4 py-3 bg-gray-900 text-gray-500 text-sm flex items-center gap-2 cursor-pointer transition-colors">
                        <Upload className="w-4 h-4" />
                        Görsel seçmek için tıklayın...
                    </div>
                </div>
                <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-400 mb-2">Footer Alt Metni</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.footerText}
                        onChange={(e) => setData({...data, footerText: e.target.value})}
                    />
                </div>
            </div>
        </div>

        {/* Contact Information */}
        <div className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
            <h2 className="text-xl font-bold text-[#C5A059] mb-6 flex items-center gap-2">
                <MapPin className="w-5 h-5" /> İletişim Bilgileri
            </h2>
            
            <div className="space-y-4">
                 <div>
                    <label className="flex items-center gap-2 text-sm font-medium text-gray-400 mb-2">
                        <MapPin className="w-4 h-4 text-[#C5A059]" /> Adres
                    </label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.address}
                        onChange={(e) => setData({...data, address: e.target.value})}
                    />
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                        <label className="flex items-center gap-2 text-sm font-medium text-gray-400 mb-2">
                            <Phone className="w-4 h-4 text-[#C5A059]" /> Telefon
                        </label>
                        <input 
                            type="text" 
                            className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                            value={data.phone}
                            onChange={(e) => setData({...data, phone: e.target.value})}
                        />
                    </div>
                    <div>
                        <label className="flex items-center gap-2 text-sm font-medium text-gray-400 mb-2">
                            <Mail className="w-4 h-4 text-[#C5A059]" /> E-posta
                        </label>
                        <input 
                            type="text" 
                            className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                            value={data.email}
                            onChange={(e) => setData({...data, email: e.target.value})}
                        />
                    </div>
                </div>
            </div>
        </div>

        {/* Social Media */}
        <div className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
            <h2 className="text-xl font-bold text-[#C5A059] mb-6 flex items-center gap-2">
                Sosyal Medya
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                 <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">LinkedIn URL</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.linkedin}
                        onChange={(e) => setData({...data, linkedin: e.target.value})}
                    />
                </div>
                 <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Instagram URL</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.instagram}
                        onChange={(e) => setData({...data, instagram: e.target.value})}
                    />
                </div>
            </div>
        </div>

        <div className="flex justify-end pt-4">
             <button
                type="submit"
                disabled={loading}
                className="bg-[#C5A059] hover:bg-[#b08e4f] text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 transition-all shadow-lg shadow-[#C5A059]/20"
            >
                <Save className="w-5 h-5" />
                {loading ? "Kaydediliyor..." : "Ayarları Kaydet"}
            </button>
        </div>

      </form>
    </div>
  );
}

"use client";

import Link from "next/link";
import { Home, Type, Image as ImageIcon, FileText, ArrowRight } from "lucide-react";

export default function ContentDashboard() {
  const sections = [
    {
      title: "Ana Sayfa Düzenle",
      description: "Hero alanı, ana başlıklar ve 'Hakkımızda' özet metni.",
      icon: Home,
      href: "/admin/content/home",
      color: "bg-blue-500",
    },
    {
      title: "Site Kimliği",
      description: "Logo, site başlığı, footer metni ve iletişim bilgileri.",
      icon: Type,
      href: "/admin/content/identity",
      color: "bg-purple-500",
    },
    {
      title: "Hizmetlerimiz Metinleri",
      description: "Hizmetler sayfasındaki açıklamaları güncelleyin.",
      icon: FileText,
      href: "/admin/content/services",
      color: "bg-green-500",
    }
  ];

  return (
    <div>
      <h1 className="text-3xl font-bold mb-2">İçerik Yönetimi (CMS)</h1>
      <p className="text-gray-400 mb-8">Sitenizin üzerindeki yazı ve görselleri buradan değiştirebilirsiniz.</p>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {sections.map((section) => (
          <Link 
            key={section.href}
            href={section.href}
            className="group bg-gray-800 p-6 rounded-2xl border border-gray-700 hover:border-[#C5A059] transition-all hover:-translate-y-1 block"
          >
            <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${section.color} bg-opacity-20 text-${section.color.replace('bg-', '')} mb-6 group-hover:scale-110 transition-transform`}>
              <section.icon className="w-6 h-6 text-white" />
            </div>
            
            <h3 className="text-xl font-bold text-white mb-2 group-hover:text-[#C5A059] transition-colors">
                {section.title}
            </h3>
            <p className="text-gray-400 mb-6 h-12">
                {section.description}
            </p>

            <div className="flex items-center gap-2 text-sm font-bold text-gray-500 group-hover:text-white transition-colors">
                Düzenle <ArrowRight className="w-4 h-4" />
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

"use client";

import { useState } from "react";
import { Save, ArrowLeft } from "lucide-react";
import Link from "next/link";

export default function ServicesEditorPage() {
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState({
    title: "ENDÜSTRİYEL ÇÖZÜMLER",
    subtitle: "EPC (Mühendislik, Tedarik, İnşaat) bazlı anahtar teslim projeler.",
    service1Title: "Doğal Çekişli Kuleler",
    service1Desc: "Termik santraller için yüksek kapasiteli, enerji tasarruflu hiperbolik betonarme yapılar.",
    service2Title: "Mekanik Çekişli Kuleler",
    service2Desc: "Hassas sıcaklık kontrolü gerektiren tesisler için fan destekli soğutma sistemleri.",
    service3Title: "Yapısal Güçlendirme",
    service3Desc: "Korozyon önleme, beton onarımı ve dolgu malzemesi değişimi ile ömür uzatma."
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    // Simulate API call
    setTimeout(() => setLoading(false), 1000);
  };

  return (
    <div className="max-w-4xl mx-auto">
       <div className="flex items-center gap-4 mb-8">
        <Link 
            href="/admin/content"
            className="p-2 bg-gray-800 rounded-lg text-gray-400 hover:text-white hover:bg-gray-700 transition-colors"
        >
            <ArrowLeft className="w-6 h-6" />
        </Link>
        <div>
            <h1 className="text-3xl font-bold">Hizmetler Metinleri</h1>
            <p className="text-gray-400">Ana sayfadaki hizmet kartlarını düzenleyin.</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-8">
        
        {/* Main Section */}
        <div className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
            <h2 className="text-xl font-bold text-[#C5A059] mb-4">Hizmetler Girişi</h2>
            <div className="space-y-4">
                 <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Başlık</label>
                    <input 
                        type="text" 
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.title}
                        onChange={(e) => setData({...data, title: e.target.value})}
                    />
                </div>
                 <div>
                    <label className="block text-sm font-medium text-gray-400 mb-2">Alt Başlık</label>
                    <textarea 
                        rows={2}
                        className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                        value={data.subtitle}
                        onChange={(e) => setData({...data, subtitle: e.target.value})}
                    />
                </div>
            </div>
        </div>

        {/* Services */}
        <div className="grid grid-cols-1 gap-6">
            {[1, 2, 3].map((num) => (
                <div key={num} className="bg-gray-800 rounded-2xl p-6 border border-gray-700">
                    <h2 className="text-lg font-bold text-white mb-4">Hizmet Kartı {num}</h2>
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Hizmet Adı</label>
                            <input 
                                type="text" 
                                className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                                value={(data as any)[`service${num}Title`]}
                                onChange={(e) => setData({...data, [`service${num}Title`]: e.target.value})}
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Kısa Açıklama</label>
                            <textarea 
                                rows={2}
                                className="w-full bg-gray-900 border border-gray-700 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-[#C5A059]"
                                value={(data as any)[`service${num}Desc`]}
                                onChange={(e) => setData({...data, [`service${num}Desc`]: e.target.value})}
                            />
                        </div>
                    </div>
                </div>
            ))}
        </div>

        <div className="flex justify-end pt-4">
             <button
                type="submit"
                disabled={loading}
                className="bg-[#C5A059] hover:bg-[#b08e4f] text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 transition-all shadow-lg shadow-[#C5A059]/20"
            >
                <Save className="w-5 h-5" />
                {loading ? "Kaydediliyor..." : "Değişiklikleri Kaydet"}
            </button>
        </div>

      </form>
    </div>
  );
}

"use client";

import { useState } from "react";
import { signIn } from "next-auth/react";
import { useRouter } from "next/navigation";
import Image from "next/image";
import { Lock, User } from "lucide-react";

export default function LoginPage() {
  const router = useRouter();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    const result = await signIn("credentials", {
      username,
      password,
      redirect: false,
    });

    if (result?.error) {
      setError("Hatalı kullanıcı adı veya şifre!");
      setLoading(false);
    } else {
      router.push("/admin");
    }
  };

  return (
    <div className="min-h-screen bg-[#111827] flex items-center justify-center p-4">
      <div className="bg-[#1F2937] p-8 rounded-2xl shadow-2xl w-full max-w-md border border-gray-700">
        <div className="text-center mb-8">
            <div className="w-20 h-20 bg-[#2D3748] rounded-2xl mx-auto flex items-center justify-center mb-4 shadow-lg border border-[#C5A059]/20">
                <span className="text-[#C5A059] text-4xl font-serif font-bold">H</span>
            </div>
          <h1 className="text-2xl font-bold text-white mb-2">HubCos Panel</h1>
          <p className="text-gray-400 text-sm">Yönetici girişi yapın</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {error && (
            <div className="bg-red-500/10 border border-red-500/50 text-red-500 text-sm p-3 rounded-lg text-center">
              {error}
            </div>
          )}

          <div className="space-y-2">
            <label className="text-sm font-medium text-gray-300 ml-1">Kullanıcı Adı</label>
            <div className="relative">
              <User className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-500" />
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full bg-[#374151] border border-gray-600 text-white rounded-xl py-3 pl-10 pr-4 focus:outline-none focus:ring-2 focus:ring-[#C5A059]/50 focus:border-[#C5A059] transition-all placeholder-gray-500"
                placeholder="Örn: admin"
              />
            </div>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium text-gray-300 ml-1">Şifre</label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-500" />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full bg-[#374151] border border-gray-600 text-white rounded-xl py-3 pl-10 pr-4 focus:outline-none focus:ring-2 focus:ring-[#C5A059]/50 focus:border-[#C5A059] transition-all placeholder-gray-500"
                placeholder="••••••"
              />
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-[#C5A059] hover:bg-[#b08e4f] text-white font-bold py-3.5 rounded-xl transition-all transform hover:scale-[1.02] disabled:opacity-50 disabled:cursor-not-allowed shadow-lg shadow-[#C5A059]/20"
          >
            {loading ? "Giriş Yapılıyor..." : "Giriş Yap"}
          </button>
        </form>
        
         <div className="mt-6 text-center text-xs text-gray-500">
            &copy; {new Date().getFullYear()} HubCos.com
        </div>
      </div>
    </div>
  );
}

"use client";

import { Users, Briefcase, MessageSquare, TrendingUp } from "lucide-react";

export default function AdminDashboard() {
  return (
    <div>
      <h1 className="text-3xl font-bold mb-8">Hoş Geldiniz, Admin</h1>
      
      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-12">
        <StatCard title="Toplam Proje" value="12" icon={Briefcase} color="bg-blue-500" />
        <StatCard title="Yeni Mesajlar" value="5" icon={MessageSquare} color="bg-green-500" />
        <StatCard title="Ziyaretçiler" value="1.2k" icon={Users} color="bg-purple-500" />
        <StatCard title="Büyüme" value="%15" icon={TrendingUp} color="bg-[#C5A059]" />
      </div>

      {/* Quick Actions or Recent Activity could go here */}
